DROP PROCEDURE IF EXISTS `PROC_AUTH`;
DELIMITER ;;
CREATE DEFINER=`userrmms`@`%` PROCEDURE `PROC_AUTH`(IN `pUserName` varchar(13),IN `pPassWord` varchar(80),IN `pIP` varchar(20))
BEGIN
	DECLARE xUserCode   VARCHAR(13) DEFAULT '';
	DECLARE xFirstName  VARCHAR(50) DEFAULT '';
	DECLARE xLastName   VARCHAR(50) DEFAULT '';
	DECLARE xUserGrp    VARCHAR(13) DEFAULT '';
	DECLARE xUserTec 	VARCHAR(13) DEFAULT '';
	DECLARE xDeptComp   VARCHAR(13) DEFAULT '';
	DECLARE xLogKey     VARCHAR(80) DEFAULT '';
	DECLARE xDept_ID     VARCHAR(80) DEFAULT '';
	DECLARE xDept_Name     VARCHAR(80) DEFAULT '';
	DECLARE xEmp_Code   VARCHAR(13) DEFAULT '';
  set xUserCode='';
	SELECT emp_user_name as UserCode,emp_firstname as FirstName,emp_lastname as LastName,Emp_Grp as UserGrp,Emp_Tec,Emp_Dept_ID,dept_name_thai,emp_code 
	Into xUserCode,xFirstName,xLastName,xUserGrp,xUserTec,xDept_ID,xDept_Name,xEmp_Code 
  FROM dev_user a left join dev_department b on a.emp_dept_id=b.dept_code where emp_user_name=pUsername and emp_password=pPassword and emp_status<>9;
  IF xUserCode='' THEN
		select 0 as mnu_grp,'Not Found User' as mnu_grp_name,0 as item_id,'' as item_name,0 as sub_item_id,'' as sub_item_label ,'' as log_key,0 as UserTec,'' as emp_code;
  ELSE
	  update log_access set log_live=0 where log_empcode=pUserName; 
		set xLogKey=md5(concat(now(),pUserName));
		Select mnu_grp,mnu_grp_name,item_id,item_label,sub_item_id,sub_item_label,log_key ,user_tec,emp_code
    From (
			SELECT 0 as mnu_grp,concat(xFirstName,'  ',xLastName) as mnu_grp_name,xDept_ID as item_id,xUserGrp as item_label,xUserCode as sub_item_id,xDept_Name as sub_item_label,xLogKey as log_key,xUserTec as user_tec,xEmp_Code as emp_code
			UNION
			SELECT a.mnu_grp,a.mnu_grp_name,b.item_id,b.item_label,c.id as sub_item_id,c.sub_item_label,xLogKey as log_key,xUserTec as user_tec,xEmp_Code as emp_code
			FROM menu_group a INNER JOIN menu_item  b ON a.mnu_grp = b.mnu_grp INNER JOIN menu_sub_item c ON b.item_id = c.item_id
			INNER JOIN menu_access d ON c.sub_item_id = d.pmenu_sub where d.puser_grp=xUserGrp
		) as QryMenu order by mnu_grp,item_id,sub_item_id;
  END IF;	#Routine body goes here...

END
;;
DELIMITER ;
